$((function(){document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",(function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))});let e=$("#scroll-to-top");$(window).scrollTop()>150&&e.fadeIn(),$(window).scroll((function(){$(this).scrollTop()>150?e.fadeIn():e.fadeOut()}))}));